// SPDX-License-Identifier: MIT

pragma solidity ^0.8.2;

// import ReentrancyGuard.sol for preventing re-entrancy attack when withdrawing
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract Payment is ReentrancyGuard {
    string public name;
    address private owner;
    uint256 public benefitAmount;
    uint32 public streamingTime;
    uint32 public totalShares;
    mapping(address => uint256) public shareOf;

    event SetNewShares(address indexed user, uint256 count);
    event SetBenefitAmount(uint256 amount);
    event Withdraw(address indexed user, uint256 shares, uint256 benefit);

    function initialize() public {
        name = "Eth & ERC20 payments";
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(owner == msg.sender, "Payment: Unauthorized");
        _;
    }

    /**
     * @dev Send allowed amount of Ether to user.
     *      User can withdraw Ether calculated in his own shares for total shares.
     */
    function withdraw() external {
        // user's shares should be existed
        require(shareOf[msg.sender] > 0, "Payment: No share");

        // calculate the amount for user
        uint256 amount = benefitAmount * shareOf[msg.sender] / totalShares;

        // calculated amount should be greater than zero
        require(amount > 0, "Payment: Benefit amount is 0");

        // transfer Ether
        payable(msg.sender).transfer(amount);

        // trigger withdraw event
        emit Withdraw(msg.sender, shareOf[msg.sender], amount);
    }

    /**
     * @dev Set the benefit amount for users.
     */
    function setBenefitAmount(uint256 _amount) external onlyOwner {
        // benefit amount should be greater than zero
        require(_amount > 0, "Payment: Amount can't be 0");

        // set the benefit amount
        benefitAmount = _amount;

        // tirgger an event
        emit SetBenefitAmount(_amount);
    }

    /**
     * @dev Set new shares to assigned user.
     */
    function setNewShares(uint32 _shareCount, address _user) external onlyOwner {
        // user's address can not be 0x0 and share count should be greater than zero
        require(_user != address(0) && _shareCount > 0, "Payment: Invalid Sharing Input");

        // add new share count to already existing users share count
        shareOf[_user] += _shareCount;
        // add new share count to total count
        totalShares += _shareCount;

        // trigger an event
        emit SetNewShares(_user, _shareCount);
    }

    /**
     * @dev Set streaming time
     */
    function setStreamingTime(uint32 _streamingTime) external onlyOwner returns (bool) {
        // streaming time should be greater than zero
        require(_streamingTime > 0, "Payment: Time can't be 0");

        streamingTime = _streamingTime;

        return true;
    }
}

